        // window.location.href = "/home"; ÎòêÎäî navigate('/home');
      }
    };

    window.addEventListener('message', handleMessage);
    return () => window.removeEventListener('message', handleMessage);
  }, []);

  return (
    <div>
      <h2 style={{ margin: '1rem 0' }}>Nomppi KB Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄ</h2>
      <iframe
        src="/login.html"
        title="ÎÇ¥ Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄ"
        style={{
          width: '100%',
          height: '800px',
          border: '1px solid #ccc',
        }}
      />
      <div style={{ marginTop: '1rem' }}>
        <button onClick={saveHtml}>Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄ HTML Ï†ÄÏû•</button>
      </div>
    </div>
  );
};

export default NomppiKBLoginPage;
